package com.lixm.notes;import android.os.Bundle;import android.text.InputFilter;import android.text.Spanned;import android.widget.EditText;import androidx.annotation.Nullable;import androidx.appcompat.app.AppCompatActivity;import java.util.regex.Matcher;import java.util.regex.Pattern;public class BActivity extends AppCompatActivity {    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_b);        EditText editText = new EditText(this);        editText.setFilters(new InputFilter[]{typeFilter});    }    InputFilter typeFilter = new InputFilter() {        @Override        public CharSequence filter(CharSequence source, int start, int end, Spanned dest, int dstart, int dend) {            Pattern p = Pattern.compile("[a-zA-Z|\u4e00-\u9fa5]+");            Matcher m = p.matcher(source.toString());            if (!m.matches()) return "";            return null;        }    };}