package com.lixm.notes.aartest;import android.os.Bundle;import android.os.Environment;import android.view.View;import androidx.annotation.Nullable;import androidx.appcompat.app.AppCompatActivity;import com.lixm.notes.R;import com.miduai.sound.utils.SoundUtils;import java.io.File;/** * Desc * Author xm li * Date :2020/7/16 */public class AarTestActivity extends AppCompatActivity {    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_aar_test);    }    public void openMp3(View view) {        String rootPath = Environment.getExternalStorageDirectory().getAbsolutePath();        final String path = rootPath + "/1111.mp3";        File file = new File(path);        if (file.exists()) {            SoundUtils.playSound(path, 3);        }    }}