package com.lixm.notes.animatorimport android.animation.ObjectAnimatorimport android.os.Bundleimport android.util.TypedValueimport android.view.Viewimport androidx.appcompat.app.AppCompatActivityimport com.lixm.notes.Rimport kotlinx.android.synthetic.main.actitvity_animator.*class AnimatorTestActivity : AppCompatActivity(){    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.actitvity_animator)    }    fun performAnimator(view: View) {        var dip2px = dip2px(50f)        var animator = ObjectAnimator.ofFloat(tvX, "translationY", 0F, -dip2px.toFloat())        animator.duration = 3000        animator.start()    }    /**     * 根据手机分辨率将dp转为px单位     */    fun dip2px(dpValue: Float): Int { //        final float scale = BaseApplication.getInstance().getResources()//                .getDisplayMetrics().density;//        return (int) (dpValue * scale + 0.5f);        return TypedValue.applyDimension(            TypedValue.COMPLEX_UNIT_DIP, dpValue,            this.getResources().getDisplayMetrics()        ).toInt()    }}