package com.lixm.notes.editimport android.os.Bundleimport android.text.InputTypeimport android.text.method.DigitsKeyListenerimport android.widget.EditTextimport androidx.appcompat.app.AppCompatActivityimport com.lixm.notes.Rclass EditTestActivity : AppCompatActivity(){    lateinit var et1 : EditText    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_edit_test)        et1 = findViewById(R.id.et1)        val regular = "1234567890ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz"        et1.setKeyListener(object : DigitsKeyListener() {            override fun getInputType(): Int {                return InputType.TYPE_TEXT_VARIATION_PASSWORD            }            override fun getAcceptedChars(): CharArray {                return regular.toCharArray()            }        })    }}