package com.lixm.notes.livedataimport android.app.Activityimport android.content.Intentimport android.os.Bundleimport android.widget.Toastimport androidx.appcompat.app.AppCompatActivityimport androidx.lifecycle.Observerimport androidx.lifecycle.ViewModelProvidersimport androidx.recyclerview.widget.LinearLayoutManagerimport androidx.recyclerview.widget.RecyclerViewimport com.lixm.notes.Rimport kotlinx.android.synthetic.main.activity_live_data.*class LiveDataDemoActivity : AppCompatActivity() {    private lateinit var wordViewModel: WordViewModel    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_live_data)        wordViewModel = ViewModelProviders.of(this).get(WordViewModel::class.java)        val recyclerView = findViewById<RecyclerView>(R.id.recyclerview)        val  adapter = WordListAdapter(this)        recyclerView.adapter = adapter        recyclerView.layoutManager = LinearLayoutManager(this)        wordViewModel.allWords.observe(this, Observer{words ->            words?.let { adapter.setWords(it) }        })        fab.setOnClickListener {            val intent = Intent(this@LiveDataDemoActivity,NewWordActivity::class.java)            startActivityForResult(intent, newWordActivityRequestCode)        }    }    companion object{        const val  newWordActivityRequestCode = 1    }    override fun onActivityResult(requestCode: Int, resultCode: Int, data: Intent?) {        super.onActivityResult(requestCode, resultCode, data)        if(requestCode == newWordActivityRequestCode && resultCode == Activity.RESULT_OK ){            data?.let {                val word = Word(it.getStringExtra(NewWordActivity.EXTRA_REPLY))                wordViewModel.insert(word)            }        }else{            Toast.makeText(                applicationContext,                R.string.empty_not_saved,                Toast.LENGTH_LONG).show()        }    }}