package com.lixm.notes.loginimport android.os.Bundleimport androidx.appcompat.app.AppCompatActivityimport com.lixm.notes.Rimport com.lixm.notes.login.util.LoginUtilsimport kotlinx.android.synthetic.main.activity_login.*/** * Desc * Author xm li * Date :2020/11/5 */class LoginActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_login)        btn_login.setOnClickListener {            Thread(object :Runnable{                override fun run() {                    Thread.sleep(3000)                    runOnUiThread {                        finish()                        LoginUtils.mILogin?.onLogin()                    }                }            }).start()        }    }}