package com.lixm.notes.loginimport android.content.Contextimport android.content.Intentimport android.os.Bundleimport android.view.Viewimport androidx.appcompat.app.AppCompatActivityimport com.lixm.notes.Rimport com.lixm.notes.login.util.LoginUtilsimport com.lxm.libutils.util.ToastUtilsimport kotlinx.android.synthetic.main.activity_login_test.*/** * Desc * Author xm li * Date :2020/11/5 */class TestLoginActivity : AppCompatActivity() ,View.OnClickListener{    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_login_test)        btn_buy.setOnClickListener(this)        btn_pay.setOnClickListener(this)    }    override fun onClick(v: View?) {        LoginUtils.setILogin(this,object : LoginUtils.ILogin{            override fun onLogin() {                when(v?.id){                    R.id.btn_buy -> {                        ToastUtils.showToast(this@TestLoginActivity,"购买")                    }                    R.id.btn_pay -> {                        ToastUtils.showToast(this@TestLoginActivity,"支付")                    }                }            }        })    }    companion object{        fun getIntent(context:Context) : Intent{            return Intent(context,TestLoginActivity::class.java)        }    }}