package com.lixm.notes.model.factory.abimport com.lixm.notes.model.factory.*class ColorFactory : AbstractFactory() {    override fun getColor(color: String): Color? {        if (color == null) {            return null        }        return when {            color.equals("RED",ignoreCase = true) -> Red()            color.equals("GREEN",ignoreCase = true) -> Green()            color.equals("BLUE",ignoreCase = true) -> Blue()            else -> null        }    }    override fun getShape(shape: String): Shape? {        return null    }}