package com.lixm.notes.model.factory.abimport com.lixm.notes.model.factory.AbstractFactoryclass FactoryProducer {    fun getFactory(choice: String): AbstractFactory? {        if (choice.equals("SHAPE", ignoreCase = true)) {            return ShapeFactory()        } else if (choice.equals("COLOR", ignoreCase = true)) {            return ColorFactory()        }        return null    }}