package com.lixm.notes.model.factory.abimport com.lixm.notes.model.factory.*class ShapeFactory : AbstractFactory() {    override fun getColor(color: String): Color? {        return null    }    override fun getShape(shapeType: String): Shape? {        if (shapeType == null) {            return null        }        return when {            shapeType.equals("CIRCLE", ignoreCase = true) -> Circle()            shapeType.equals("RECTANGLE", ignoreCase = true) -> Rectangle()            shapeType.equals("SQUARE", ignoreCase = true) -> Square()            else -> null        }    }}