package com.lixm.notes.model.factory.adapterclass MediaAdapter(audioType: String) : MediaPlayer {    var advanceMusicPlayer: AdvancedMediaPlayer? = null    init {        if(audioType.equals("vlc",ignoreCase = true)){            advanceMusicPlayer = VlcPlayer()        }else if(audioType.equals("mp4",ignoreCase = true)){            advanceMusicPlayer = Mp4Player()        }    }    override fun play(audioType: String, fileName: String) {        if(audioType.equals("vlc",ignoreCase = true)){            advanceMusicPlayer?.playVlc(fileName)        }else if(audioType.equals("mp4",ignoreCase = true)){            advanceMusicPlayer?.playMp4(fileName)        }    }}