package com.lixm.notes.model.factory.appearanceclass ShapeMaker {    private var circle: Circle? = null    private var rectangle: Rectangle? = null    private var square: Square? = null    init {        circle = Circle()        rectangle = Rectangle()        square = Square()    }    fun drawCircle(){        circle?.draw()    }    fun drawRectangle(){        rectangle?.draw()    }    fun drawSquare(){        square?.draw()    }}