package com.lixm.notes.model.factory.combinationclass Employee(name: String, dept: String, sal: Int) {    var name: String? = null    var dept: String? = null    var salary: Int = 0    var subordinates: MutableList<Employee>? = null    init {        this.name = name        this.dept = dept        this.salary = sal        subordinates = mutableListOf()    }    fun add(e: Employee){        subordinates?.add(e)    }    fun remove(e: Employee){        subordinates?.remove(e)    }    override fun toString(): String {        return "Employee : [ Name:$name, dept : $dept, salary :$salary"    }}