package com.lixm.notes.model.factory.filterclass AndCriteria(criteria: Criteria, otherCriteria: Criteria) : Criteria {    var criteria: Criteria? = null    var otherCriteria: Criteria? = null    init {        this.criteria = criteria        this.otherCriteria = otherCriteria    }    override fun meetCriteria(persons: List<Person>?): List<Person>? {        var firstCriteriaPerson: List<Person>? = criteria?.meetCriteria(persons)        return otherCriteria?.meetCriteria(firstCriteriaPerson)    }}