package com.lixm.notes.model.factory.filterclass CriteriaFemale : Criteria {    override fun meetCriteria(persons: List<Person>?): List<Person>? {        if(persons == null) return null        var femalePersons = ArrayList<Person>()        for (person in persons){            if(person.gender.equals("FEMALE",ignoreCase = true)){                femalePersons.add(person)            }        }        return femalePersons    }}