package com.lixm.notes.model.factory.filterclass CriteriaMale : Criteria {    override fun meetCriteria(persons: List<Person>?): List<Person>? {        if(persons == null) return null        var malePersons = ArrayList<Person>()        for(person in persons){            if(person.gender.equals("MALE",ignoreCase = true)){                malePersons.add(person)            }        }        return malePersons    }}