package com.lixm.notes.model.factory.filterclass CriteriaSingle : Criteria {    override fun meetCriteria(persons: List<Person>?): List<Person>? {        if(persons == null) return null        var singlePersons = ArrayList<Person>()        for(person in persons){            if(person.maritalStatus.equals("SINGLE",ignoreCase = true)){                singlePersons.add(person)            }        }        return singlePersons    }}