package com.lixm.notes.model.factory.filterclass OrCriteria(criteria: Criteria,otherCriteria: Criteria) : Criteria {    var criteria : Criteria? = null    var otherCriteria: Criteria? = null    init {        this.criteria = criteria        this.otherCriteria = otherCriteria    }    override fun meetCriteria(persons: List<Person>?): List<Person>? {        var firstCriteriaItems: ArrayList<Person>? = criteria?.meetCriteria(persons) as ArrayList<Person>?        var otherCriteriaItems: ArrayList<Person>? = otherCriteria?.meetCriteria(persons) as ArrayList<Person>?        if(otherCriteriaItems == null) return null        for(person in otherCriteriaItems){            if(firstCriteriaItems?.contains(person) != false){                firstCriteriaItems?.add(person)            }        }        return firstCriteriaItems    }}