package com.lixm.notes.model.factory.flyweightclass Circle(color: String): Shape {    var color: String? = null    var x: Int = 0    var y: Int = 0    var radius: Int = 0    init {        this.color = color    }    override fun draw() {        println(            "Circle: Draw() [Color : " + color    + ", x : " + x + ", y :" + y + ", radius :" + radius        )    }}