package com.lixm.notes.model.factory.flyweightobject ShapeFactory {    val circleMap: HashMap<String,Shape> = HashMap()    fun getCircle(color: String): Shape{        var circle = circleMap[color]        if(circle == null){            circle = Circle(color)            circleMap[color] = circle            println("Creating circle of color : \" + color")        }        return circle    }}