package com.lixm.notes.model.factory.prototypeimport java.util.*object ShapeCache {    private val shapeMap = Hashtable<String, Shape>()    fun getShape(shapeId: String): Shape {        val cachedShape = shapeMap[shapeId]        return cachedShape?.clone() as Shape    }    fun loadCache(){        var circle = Circle()        circle.id = "1"        shapeMap[circle.id] = circle        var square = Square()        square.id = "2"        shapeMap[square.id] = square        var rectangle = Rectangle()        rectangle.id = "3"        shapeMap[rectangle.id] = rectangle    }}