package com.lixm.notes.model.factory.singletonobject Singleton {    @Volatile    private var singleton : Singleton? = null    fun getSingleton(): Singleton? {        if(singleton == null){            synchronized(Singleton::class.java){                if(singleton == null){                    singleton = Singleton                }            }        }        return singleton    }    fun showMsg(){        print("==>msg")    }}