package com.lixm.notes.processimport android.os.Bundleimport android.view.Viewimport androidx.appcompat.app.AppCompatActivityimport com.lixm.notes.Rimport kotlinx.android.synthetic.main.activity_activity_process.*class ActivityProcessActivity: AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_activity_process)        if(intent.data != null){            var host = intent.data.host            var bundle = intent.extras            var value = bundle.getString("value")            tvValue.text = value        }    }    fun closePage(view : View){        intent.putExtra("result","关闭页面成功")        setResult(2,intent)        finish()    }}