package com.lixm.notes.redbook.adapter;import android.content.Context;import android.view.View;import android.view.ViewGroup;import androidx.viewpager.widget.PagerAdapter;import java.util.LinkedList;import static android.view.ViewGroup.LayoutParams.MATCH_PARENT;/** * Desc * Author xm li * Date :2021/2/23 */public class ParallaxPagerAdapter extends PagerAdapter {    private int count = 0;    private final Context context;    private final LinkedList<View> recycleBin = new LinkedList<View>();    public ParallaxPagerAdapter(Context context) {        this.context = context;    }    public void setCount(int count) {        this.count = count;    }    @Override public int getCount() {        return count;    }    @Override public Object instantiateItem(ViewGroup container, int position) {        View view;        if (!recycleBin.isEmpty()) {            view = recycleBin.pop();        } else {            view = new View(context);            view.setLayoutParams(new ViewGroup.LayoutParams(MATCH_PARENT, MATCH_PARENT));        }        container.addView(view);        return view;    }    @Override public void destroyItem(ViewGroup container, int position, Object object) {        View view = (View) object;        container.removeView(view);        recycleBin.push(view);    }    @Override public boolean isViewFromObject(View view, Object object) {        return view.equals(object);    }}