package com.lixm.notes.redbook.widget;import android.content.Context;import android.view.LayoutInflater;/** * Desc * Author xm li * Date :2021/2/22 */public class ParallaxLayoutInflater extends LayoutInflater {    protected ParallaxLayoutInflater(LayoutInflater original, Context newContext) {        super(original, newContext);        setUpLayoutFactory();    }    private void setUpLayoutFactory() {        if (!(getFactory2() instanceof ParallaxFactory)) {            setFactory2(new ParallaxFactory(this, getFactory2()));        }    }    @Override    public LayoutInflater cloneInContext(Context newContext) {        return new ParallaxLayoutInflater(this, newContext);    }}