package com.lixm.notes.viewmodelimport android.os.Bundleimport androidx.fragment.app.Fragmentimport androidx.lifecycle.ViewModelProvidersclass DetailFragment : Fragment() {    private lateinit var model: SharedViewModel    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        model = activity?.run {            ViewModelProviders.of(this)[SharedViewModel::class.java]        }?: throw Exception("Invalid Activity")    }}