package com.lixm.notes.work_managerimport android.content.Contextimport android.graphics.BitmapFactoryimport android.net.Uriimport android.text.TextUtilsimport androidx.work.Workerimport androidx.work.WorkerParametersimport androidx.work.workDataOfimport timber.log.Timberclass BlurWorker(ctx: Context,params: WorkerParameters) : Worker(ctx,params) {    override fun doWork(): Result {        val appContext = applicationContext        val resourceUri = inputData.getString(KEY_IMAGE_URI)        makeStatusNotifiaction("Blurring image",appContext)//        sleep()        return try {            if(TextUtils.isEmpty(resourceUri)){                Timber.e("Invalid input uri")                throw IllegalArgumentException("Invalid input uri")            }            val resolver = appContext.contentResolver            var picture = BitmapFactory.decodeStream(                resolver.openInputStream(Uri.parse(resourceUri)))            val output = blurBitmap(picture,appContext)            val outputUri = writeBitmapToFile(appContext,output)            makeStatusNotifiaction("Output is $outputUri",appContext)            val outputData = workDataOf(KEY_IMAGE_URI to outputUri.toString())            Result.success(outputData)        }catch (throwable: Throwable){            Timber.e(throwable)            Result.failure()        }    }}