package com.lixm.notesimport org.junit.Testimport kotlin.random.Randomclass ScopeFunctionTest {    @Test    fun scopeFunctionTest1(){        Person("Alice",20,"Amsterdam").let {            println(it)            it.moveTo("London")            it.incrementAge()            println(it)        }    }    @Test    fun scopeFunctionTest2(){        val str = "Hello"        str.run {            println("The receiver string length: $length")            println("The receiver string length: ${this.length}")        }        str.let {            println("The receiver string length: ${it.length}")        }    }    @Test    fun scopeFunctionTest3(){        val adam = Person("Adam").apply {            age = 20            city = "London"        }        println(adam)    }    @Test    fun scopeFunctionTest4(){        fun getRandomInt(): Int{            return Random.nextInt(100).also {                writeToLog("getRandomInt() generated value $it")            }        }        val i = getRandomInt()    }}fun writeToLog(message: String){    println("INFO: $message")}data class Person(var name: String,var age: Int = 0,var city: String = ""){    fun moveTo(newCity: String){city = newCity}    fun incrementAge(){ age++}}