package com.lixm.notes.kotlinclass Address {    var name: String = "Holmes,Sherlock"    var street: String = "Baker"    var city: String = "London"    var state: String? = null    var zip: String = "12345"//    val simple: Int?//    val inferredType = 1    val isEmpty: Boolean        get() = true    var stringRepresentation: String        get() = this.toString()        set(value){            ""+1        }    var setterVisibility: String = "abc"        private set//    var setterWithAnnotation: Any? = null//        @Inject set    var counter = 0        set(value) {            if(value >= 0) field = value        }    private var _table: Map<String,Int>? = null    public val table: Map<String,Int>        get() {            if(_table == null){                _table = HashMap()            }            return _table ?: throw AssertionError("set to null by antother  thread")        }}fun copyAddress(address: Address): Address{    val result = Address()    result.name = address.name    result.street = address.street    return result}