package com.lixm.notes.kotlin.commissionimport kotlin.properties.Delegatesopen class Example {    var p: String by Delegate()    val lazyValue: String by lazy(LazyThreadSafetyMode.NONE) {        println("computed!")        "hello"    }    var name: String by Delegates.observable("<no name>") { property, oldValue, newValue ->        println("$property->$oldValue -> $newValue")    }}